char Free = ' ';
char Occupied = '@';
char Banned = '/';
char Start = 'S';
char Final = 'F';

void afiseaza()
{
    for (int i = 0; i < ilabrn; i++)
        for (int j = 0; j < jlabrn; j++)
            if(labrn[i][j]==Banned)
                labrn[i][j]=Free;
    for (int i = 0; i < ilabrn; i++)
        cout<<labrn[i]<<endl;
    cout<<endl;
}

bool rezolva(int x, int y)
{
    if (labrn[x][y] == Final)
        return true;

    if(labrn[x][y]!=Start)
        labrn[x][y] = Occupied;

    if (y > 0 && (labrn[x][y - 1] == Free || labrn[x][y - 1] == Final) && rezolva(x, y-1))
        return true;

    if (y < jlabrn && (labrn[x][y + 1] == Free || labrn[x][y + 1] == Final) && rezolva(x, y+1))
        return true;

    if (x > 0 && (labrn[x - 1][y] == Free || labrn[x - 1][y] == Final) && rezolva(x-1, y))
        return true;

    if (x < ilabrn && (labrn[x + 1][y] == Free
 || labrn[x + 1][y] == Final) && rezolva(x+1, y))
        return true;
    labrn[x][y] = Banned;
    return false;
}

int main()
{
    int arr[2], y, x, sem=0;
    for (int i = 0; i < ilabrn && sem==0; i++)
        for (int j = 0; j < jlabrn && sem ==0; j++)
            if(labrn[i][j]==Start)
            {
                x = i;
                y = j;
                sem = 1;
            }
    if (rezolva(x, y))
    {
        afiseaza();
        cout<<"Succes!";
    }
    else cout<<"No solution!";
}
